+    bool m_error_flag{false};
+};
+
+void FileAdder::Run(CSHA512& hasher)
 {
+    if (m_error_flag) return;
     struct stat sb = {};
-    int f = open(path, O_RDONLY);
+    int f = open(m_path.c_str(), O_RDONLY);
     size_t total = 0;
     if (f != -1) {
         unsigned char fbuf[4096];
@@ -111,6 +130,8 @@ void AddFile(CSHA512& hasher, const char *path)
             /* not bothering with EINTR handling. */
         } while (n == sizeof(fbuf) && total < 1048576); // Read only the first 1 Mbyte
         close(f);
+    } else {
+        m_error_flag = true;
     }
 }
 
